import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { DatePicker } from "@mui/lab";
import { CircularProgress, Rating, TextField } from "@mui/material";
import styled from "styled-components";
import { useDispatch } from "react-redux";
import { bookProperty } from "../api";
import { openSnackbar } from "../redux/reducers/snackbarSlice";
import Button from "../componnents/Button";

const Container = styled.div`
  display: flex;
  flex-direction: row;
  gap: 50px;
  padding: 20px;
  height: 95vh;
  margin: 0 20px;
  background: ${({ theme }) => theme.bg};
  border-radius: 12px 12px 0 0;
  overflow-y: scroll;
`;

const Image = styled.img`
  width: 50%;
  border-radius: 6px;
  object-fit: cover;
`;

const Right = styled.div`
  display: flex;
  flex-direction: column;
  gap: 24px;
`;

const Title = styled.h1`
  font-size: 24px;
  font-weight: 700;
  color: ${({ theme }) => theme.text_primary};
`;

const Desc = styled.p`
  font-size: 16px;
  color: ${({ theme }) => theme.text_primary};
`;

const Price = styled.div`
  font-size: 20px;
  font-weight: 500;
  color: ${({ theme }) => theme.text_primary};
`;

const Span = styled.span`
  font-size: 16px;
  color: ${({ theme }) => theme.text_secondary};
  text-decoration: line-through;
  margin-left: 8px;
`;

const Percent = styled.span`
  font-size: 16px;
  color: green;
  margin-left: 8px;
`;

const RatingContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
`;

const BookingContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const PropertyDetails = () => {
const [startDate, setStartDate] = useState(null);
const [endtDate,setEndDate] = useState(null);
const [property , setProperty] = useState();
const [loading, setLoading] = useState(false);



  return ( <Container>
    <Image  src={property?.img}/>
    <Right>
      <Title>{property?.title}</Title>
      <Desc>{property?.desc}</Desc>
      <Price>
        ${property?.price?.org}
        <Span>${property?.price?.mrp}</Span>
        <Percent>({property?.price?.off}% off)</Percent>  
      </Price>
      <RatingContainer>
        <Rating value={property?.rating} readOnly/>
        <span>({property?.rating})</span>
      </RatingContainer>
      <BookingContainer>
       <DatePicker
       lable="Start Date"
        renderInput={(params) => <TextField {...params} />}
       /> 
        <DatePicker 
        lable="End Date"
        renderInput={(params) => <TextField {...params} />}
        />

        <Button 
        variant="contained"
        color="secondary"
        text="Book Now"
        > </Button>


      </BookingContainer>
    </Right>
  </Container>
  );
};

export default PropertyDetails;
