import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { CircularProgress, Rating } from "@mui/material";
import styled from "styled-components";
import {
  FavoriteBorder,
  FavoriteRounded,
} from "@mui/icons-material";
import { useDispatch } from "react-redux";
import { openSnackbar } from "../../redux/reducers/snackbarSlice";
import {
  addToFavourite,
  deleteFromFavourite,
  getFavourite,
} from "../../api";

// Styled Components
const Card = styled.div`
  width: 250px;
  display: flex;
  flex-direction: column;
  cursor: pointer;
  background: white;
  border-radius: 10px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  overflow: hidden;
  transition: transform 0.3s ease;

  &:hover {
    transform: translateY(-4px);
  }
`;

const ImageWrapper = styled.div`
  position: relative;
  width: 100%;
  height: 250px;
  background: #f5f5f5;
  overflow: hidden;
`;

const Image = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.3s ease;

  &:hover {
    transform: scale(1.05);
  }
`;

const Menu = styled.div`
  position: absolute;
  z-index: 10;
  top: 12px;
  right: 12px;
  opacity: 0;
  transition: opacity 0.3s ease;
  ${ImageWrapper}:hover & {
    opacity: 1;
  }
`;

const MenuItem = styled.div`
  border-radius: 50%;
  width: 32px;
  height: 32px;
  background: white;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.15);
  transition: transform 0.2s ease;

  &:hover {
    transform: scale(1.1);
  }
`;

const Rate = styled.div`
  position: absolute;
  z-index: 10;
  bottom: 12px;
  left: 12px;
  padding: 4px 8px;
  border-radius: 6px;
  background: white;
  display: flex;
  align-items: center;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.15);
`;

const Details = styled.div`
  padding: 16px;
  display: flex;
  flex-direction: column;
  gap: 8px;
`;

const Title = styled.div`
  font-size: 16px;
  font-weight: 600;
  color: ${({ theme }) => theme?.text_primary || '#222222'};
`;

const Description = styled.div`
  font-size: 14px;
  color: ${({ theme }) => theme?.text_secondary || '#717171'};
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
`;

const Price = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  margin-top: 4px;
`;

const Amount = styled.span`
  font-size: 16px;
  font-weight: 600;
  color: ${({ theme }) => theme?.text_primary || '#222222'};
`;

const Strike = styled.span`
  font-size: 14px;
  font-weight: 400;
  text-decoration: line-through;
  color: ${({ theme }) => theme?.text_secondary || '#717171'};
`;

const Percent = styled.span`
  font-size: 12px;
  font-weight: 500;
  color: #00a650;
  background: #e6f4ea;
  padding: 2px 6px;
  border-radius: 4px;
`;

const PropertyCard = ({ property }) => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [isFavorite, setIsFavorite] = useState(false);
  const [loading, setLoading] = useState(false);

  const handleClick = () => {
    if (property?._id) {
      navigate(`/property/${property._id}`);
    }
  };

  const handleFavorite = async (e) => {
    e.stopPropagation();
    if (!property?._id) return;

    setLoading(true);
    try {
      if (isFavorite) {
        await deleteFromFavourite(property._id);
        setIsFavorite(false);
        dispatch(openSnackbar({ message: "Removed from favorites!", severity: "success" }));
      } else {
        await addToFavourite(property._id);
        setIsFavorite(true);
        dispatch(openSnackbar({ message: "Added to favorites!", severity: "success" }));
      }
    } catch (error) {
      dispatch(openSnackbar({ 
        message: error.message || "Error updating favorites", 
        severity: "error" 
      }));
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const checkFavorite = async () => {
      if (!property?._id) return;

      try {
        const favorites = await getFavourite();
        setIsFavorite(favorites?.some(fav => fav._id === property._id));
      } catch (error) {
        console.error("Error checking favorites:", error);
      }
    };
    checkFavorite();
  }, [property]);

  if (!property) return null;

  return (
    <Card onClick={handleClick}>
      <ImageWrapper>
        <Image 
          src={property.img} 
          alt={property.title || 'Property'} 
          onError={(e) => {
            e.target.src = '/placeholder.jpg';
          }}
        />
        <Menu>
          <MenuItem onClick={handleFavorite}>
            {loading ? (
              <CircularProgress size={20} />
            ) : isFavorite ? (
              <FavoriteRounded sx={{ fontSize: "18px", color: "red" }} />
            ) : (
              <FavoriteBorder sx={{ fontSize: "18px", color: "#222222" }} />
            )}
          </MenuItem>
        </Menu>
        <Rate>
          <Rating 
            value={Number(property.rating) || 0} 
            sx={{ fontSize: "14px" }} 
            readOnly 
            precision={0.5}
          />
        </Rate>
      </ImageWrapper>
      <Details>
        <Title>{property.title}</Title>
        <Description>
          {property.location ? `A stunning house located near ${property.location}` : 'Location not available'}
        </Description>
        <Price>
          <Amount>${property.price?.org || 0}</Amount>
          {property.price?.mrp && <Strike>${property.price.mrp}</Strike>}
          {property.price?.off && <Percent>{property.price.off}% OFF</Percent>}
        </Price>
      </Details>
    </Card>
  );
};

export default PropertyCard;
